import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class DataUsers extends React.Component{
    state = {
        users : [],
        no : 1
    }

    componentDidMount(){

      //   fetch("http://apiflutter2.000webhostapp.com/tampil.php")
      // .then(res => res.json())
      // .then(
      //   (result) => {
      //     this.setState({
      //       users: result.data
      //     });
      //   },
      //   // Note: it's important to handle errors here
      //   // instead of a catch() block so that we don't swallow
      //   // exceptions from actual bugs in components.
      //   (error) => {
      //     this.setState({
      //       isLoaded: true,
      //       error
      //     });
      //   }
      // )

        axios.get(`http://apiflutter2.000webhostapp.com/tampil.php`)
      .then(res => {
        const users = res.data;
        this.setState({ users });
      });
       //console.log(this.state);
    }

    render(){
        return(
            <Container className="mt-3">
                <Row>
                    <Col xs={12} lg={12} md={12} >
                        <Button href="/adduser" variant="success">Add User</Button>

                        <Table striped bordered hover className="mt-2" >
                            <thead>
                                <tr>
                                    <th>Id User</th>
                                    <th>Nama</th>
                                    <th>Username</th>
                                    <th>Password</th>
                                </tr>
                            </thead>
                            <tbody>
                                { this.state.users.map((user) => 
                                    <tr key={Math.random()}>
                                        <td >{ user.iduser }</td>
                                        <td >{ user.nama }</td>
                                        <td >{ user.username }</td>
                                        <td >{ user.password }</td>
                                    </tr>
                                    ) }
                            </tbody>
                        </Table>
                    </Col>
                </Row>
            </Container>
        )
    }
}
