import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class DataUsers extends React.Component{
  state = {
    users : [],
    id: "",
    nama : "",
    username : "",
    password : "",
    isUpdate : false
  }

  getUserAPI = () => {
     axios.get(`http://localhost/test_api/tampil.php`)
      .then(res => {
        const users = res.data;
        this.setState({ users });
      });
  }
    componentDidMount(){
       this.getUserAPI();
       //console.log(this.state);
    }

    handleEdit = (data) => {
      this.setState({
        id : data.id, 
        nama : data.nama,
        username : data.username,
        password : data.password
      })
    }

    handleSubmit = () => {

      const dataUser = {
        nama : this.state.nama,
        username : this.state.username,
        password : this.state.password,
      };
      axios.post(`http://localhost/test_api/tambah.php`,{dataUser})
      .then(res => {
        if(res.status === 200){
          this.getUserAPI();
        }else{
          console.log("Gagal Input!!");
        }
      })
    }

    handleChange = (e) => {
      this.setState({ [e.target.name] : e.target.value });
    }

    handleDelete = (id) => {
      axios.delete(`http://localhost/test_api/delete.php?iduser=${id}`)
            .then(res => {
              if(res.status === 200){
                this.getUserAPI();
              }else{
                alert('Gagal Delete!!');
              }
            })
    }

    render(){
        return(
            <Container className="mt-3">
                <Row>
                    <Col xs={12} lg={12} md={12} >

                        <Col xs={12} lg={12} md={12} >
                          <Form>
                            <Form.Group controlId="formBasicEmail">
                              <Form.Label>Nama</Form.Label>
                              <Form.Control name="nama" onChange={this.handleChange} type="text" placeholder="Nama" value={this.state.nama} />
                            </Form.Group>
                            <Form.Group controlId="formBasicPassword">
                              <Form.Label>Username</Form.Label>
                              <Form.Control name="username" type="text" onChange={this.handleChange} placeholder="Username" value={this.state.usernama}/>
                            </Form.Group>
                            <Form.Group controlId="formBasicPassword">
                              <Form.Label>Password</Form.Label>
                              <Form.Control name="password" type="text" onChange={this.handleChange} placeholder="Password" value={this.state.password}/>
                            </Form.Group>
                            <Button variant="primary" onClick={this.handleSubmit}>
                              Submit
                            </Button>
                          </Form>
                        </Col>

                        <Table striped bordered hover className="mt-2" >
                            <thead>
                                <tr>
                                    <th>Id User</th>
                                    <th>Nama</th>
                                    <th>Username</th>
                                    <th>Password</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                { this.state.users.map((user) => 
                                    <tr key={Math.random()}>
                                        <td >{ user.iduser }</td>
                                        <td >{ user.nama }</td>
                                        <td >{ user.username }</td>
                                        <td >{ user.password }</td>
                                        <td> 
                                          <Button variant="warning" onClick={this.handleEdit(user.iduser)} >Edit</Button> 
                                          <Button variant="danger"  >Hapus</Button>
                                        </td>
                                    </tr>
                                    ) }
                            </tbody>
                        </Table>
                    </Col>
                </Row>
            </Container>
        )
    }
}
