import React from 'react';
import axios from 'axios';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class AddUser extends React.Component{

constructor() {
    super();
	this.state = {
		nama : "",
		username : "",
		password : ""
	}
	this.handleChange = this.handleChange.bind(this);
	this.handleSubmit = this.handleSubmit.bind(this);
}

handleChange(e){
	this.setState({ [e.target.name] : e.target.value });
	e.preventDefault()
}

handleSubmit(e){
	e.preventDefault()

	fetch("http://apiflutter2.000webhostapp.com/tambah.php",{
            method: 'POST',
            body:JSON.stringify({
                nama: this.state.nama,
                username: this.state.username,
                password: this.state.password
            }),
            headers:{
                'Content-Type': 'application/json',
                'Access-Control-Allow-Origin': '*'
            }
        }).then(response => {
            if(response.status === 200){
                alert("input berhasil");
                //this.componentDidMount()
                this.kosong()
                //console.log("ini respon submit",response );
            }
        }).catch(error=>{console.log("errornya..",error);});

	/*const dataUser = {
		nama : this.state.nama,
		username : this.state.username,
		password : this.state.password,
	};

	axios.post("http://apiflutter2.000webhostapp.com/tambah.php",{ dataUser })
	.then(res => {
		console.log(res);
	})*/
}

render(){
	return(
		<Container className="mt-3" >
			<Row>
				<Col xs={12} lg={12} md={12} >
					<Form onSubmit={this.handleSubmit}>
					  <Form.Group controlId="formBasicEmail">
					    <Form.Label>Nama</Form.Label>
					    <Form.Control name="nama" onChange={this.handleChange} type="text" placeholder="Nama" />
					  </Form.Group>
					  <Form.Group controlId="formBasicPassword">
					    <Form.Label>Username</Form.Label>
					    <Form.Control name="username" type="text" onChange={this.handleChange} placeholder="Username" />
					  </Form.Group>
					  <Form.Group controlId="formBasicPassword">
					    <Form.Label>Password</Form.Label>
					    <Form.Control name="password" type="text" onChange={this.handleChange} placeholder="Password" />
					  </Form.Group>
					  <Button variant="primary" type="submit">
					    Submit
					  </Button>
					</Form>
				</Col>
			</Row>
		</Container>
	)
}

}
