import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class DataUsers extends React.Component{
  state = {
      users : []
  }

    componentDidMount(){
        axios.get(`http://localhost/test_api/tampil.php`)
      .then(res => {
        const users = res.data;
        this.setState({ users });
      });
       //console.log(this.state);
    }

    handleEdit(id){

    }

    handleDelete(e,id){
      e.preventDefault()
      axios.delete('http://localhost/test_api/delete.php?iduser=${id}')
           .then(res => {
              console.log(res);
            })
    }

    render(){
        return(
            <Container className="mt-3">
                <Row>
                    <Col xs={12} lg={12} md={12} >
                        <Button href="/adduser" variant="success">Add User</Button>

                        <Table striped bordered hover className="mt-2" >
                            <thead>
                                <tr>
                                    <th>Id User</th>
                                    <th>Nama</th>
                                    <th>Username</th>
                                    <th>Password</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                { this.state.users.map((user) => 
                                    <tr key={Math.random()}>
                                        <td >{ user.iduser }</td>
                                        <td >{ user.nama }</td>
                                        <td >{ user.username }</td>
                                        <td >{ user.password }</td>
                                        <td> 
                                          <Button variant="warning" onClick={this.handleEdit()} >Edit</Button> 
                                          <Button variant="danger" onClick={(e) => this.handleDelete( e,user.iduser )} >Hapus</Button>
                                        </td>
                                    </tr>
                                    ) }
                            </tbody>
                        </Table>
                    </Col>
                </Row>
            </Container>
        )
    }
}
